import {
  Form,
  Row,
  Col
} from 'react-bootstrap'

export const NumberInput = function ({
  formGroupClass = "",
  formLabelClass = "",
  colSpan = 12,
  validationFunc = () => true,
  label = "",
  placeholderText = "",
  textmuted = null,
  value = undefined,
  onChange = () => { },
  isDisabled = false
}) {

  const handleChange = (val) => {
    console.log("start validation")
    if (validationFunc(val)) {
      console.log("validation OK")
      onChange(val)
      return
    }
    console.log("validation FAIL")
  }

  return (
    <Form.Group className={formGroupClass}>
      {(label && label.length > 0) && <Form.Label className={formLabelClass}>{label}</Form.Label>}
      <Row className="w-100 ml-0 mr-0">
        <Col md={colSpan} className="pl-0 pr-0">
          <Form.Control
            type="number"
            placeholder={placeholderText}
            value={value}
            onChange={(event) => handleChange(event.target.value)}
            disabled={isDisabled}
          />
        </Col>
      </Row>
      {textmuted && <Form.Text className="text-muted">
        {textmuted}
      </Form.Text>}
    </Form.Group>
  )
}

